mapper.select:
select movie_id,room_id,time_start,time_end, showtimes_detail.id as id, date from showtimes_detail
inner join movie m on showtimes_detail.movie_id = m.id
and movie_id = #{movieId}
and room_id = #{roomId}
and time_start = #{timeStart}
and date = #{dateStart}
and m.name like concat('%',#{search},'%')
order by
id
showtimes_id
movie_id
room_id
date
time_start
DESC
limit #{limit} offset #{offset}

select count(*) from showtimes_detail

    left join movie m on showtimes_detail.movie_id = m.id



        and movie_id = #{movieId}


        and room_id = #{roomId}


        and time_start = #{timeStart}


        and date = #{dateStart}


        and m.name like concat('%',#{search},'%')

mapper.insert:
insert into booksystem.showtimes_detail(time_end, showtimes_id, movie_id, location_id, theater_id, room_id, time_start)
values (#{entity.timeEnd},#{entity.showTimesId},#{entity.movieId},#{entity.locationId},#{entity.theaterId},#{entity.roomId},#{entity.timeStart})

        SELECT LAST_INSERT_ID();

mapper.update:
update showtimes_detail


        movie_id = #{entity.movieId},


        room_id = #{entity.roomId},


        time_start = #{entity.timeStart}


where id = #{entity.id}
