mapper.insert:
insert into booksystem.theater(code, name, location_id, latitude, longitude)

VALUES (#{entity.code},#{entity.name},#{entity.locationId},#{entity.latitude},#{entity.longitude})


    SELECT LAST_INSERT_ID();

mapper.update:
update theater set code = #{theater.code}, name = #{theater.name},
                   latitude = #{theater.latitude},
                   longitude = #{theater.longitude}
        where id = #{theater.id}
